'CREATE OR REPLACE FORCE VIEW "EXM_CARDHOLDERS_V" ("NAME", "PERSON_ID") AS ',
'  with t as',
'(select blob001 as from APEX_COLLECTIONS where COLLECTION_NAME = ''BIP_CARDHOLDER_NAMES'')',
'select ',
'x."NAME",x."PERSON_ID"',
' FROM t,',
'xmltable(''ROWSET/ROW'' ',
'         passing xmltype(t.blob001, 1)',
'              columns',
'              NAME  path ''DIS_VAL'',',
'              PERSON_ID NUMBER path ''RET_VAL''',
'                 ) x ',
'UNION',
'select "DIS_VAL","RET_VAL" from (',
'SELECT a.cardmember_name DIS_VAL, a.PERSON_ID RET_VAL',
'FROM  EXM_CARDS a,',
'      EXM_CARD_PROGRAMS b',
'where a.card_program_id=b.card_program_id',
'AND B.card_type_lookup_code= ''TRAVEL'' order by a.PERSON_ID)',
';',
'',
'CREATE OR REPLACE FORCE VIEW "EXM_LOAD_SUMMARY_V" ("VALUE", "YEAR", "MONTH", "PERIOD") AS ',
'  (select count(*) VALUE, "Year" YEAR, "Month" MONTH, "Month"||''-''||"Year" PERIOD',
'from',
'(',
'SELECT',
'  count(*),',
' instance,',
'created_by,',
'trunc(creation_date) CREATION_DATE,',
'TO_NUMBER(TO_CHAR(trunc(creation_date),''fmMM'')) "Month",',
'TO_CHAR(trunc(creation_date),''fmYY'') "Year"',
'FROM EXM_CREDIT_CARD_TRXNS',
'WHERE CREATED_BY NOT IN (''JOHN.KOLDEN@ORACLE.COM'', ''NICK.DENNISON@ORACLE.COM'')',
